int getEdgeIndex(int fromNode, int toNode)
{
    int k = 0;
    while(networkEdges[k][0] != fromNode && networkEdges[k][1] != toNode)
    {
        k++;
    }
    return k;
}

template.declaration:
clock x;

void constructor()
{

}

int getNextRouteNode(int carId, int node)
{
    int i = 0;
    while(route[carId][i] != node && route[carId][i] != -1)
    {
        i++;
    }
    return route[carId][i+1];
}

//check if cars have changed edge
void flow_traffic()
{
    int i = 0, j = 0, k = 0;
    int currentEdge = 0;
    int timePassedCarI = 0;
    int edgeTT = 0;
    int edgeId;
    int fromNode, toNode;    


    for (i = 0; i < N; i++)
    {
        timePassedCarI = timePassed[i];

        while(route[i][j] != currentStartNode[i] && route[i][j] != -1)
        {
            j++;
        }
        fromNode = route[i][j];
        toNode = route[i][j+1];

        /*
        // ensure that the nodes are not intermediate nodes
        if(fromNode >= firstIntermediateNode)
        {
            fromNode = route[i][j-1];
        }

        if(toNode >= firstIntermediateNode)
        {
            toNode = route[i][j+2];
        }
        */
        edgeId = getEdgeIndex(fromNode, toNode);



        edgeTT = networkEdges[edgeId][3];

        if(timePassedCarI >= edgeTT)
        {
            currentStartNode[i] = toNode;
            timePassed[i] = 0;
        }


    }
}

int get_cars_on_edge(int edgeId)
{
    int i = 0, j = 0;
    int fromNode = 0, toNode = 0;
    int carsOnEdge = 0;
    for(i = 0; i < N; i++)
    {
        fromNode = currentStartNode[i];
        toNode = getNextRouteNode(i, fromNode);

        if(edgeId == getEdgeIndex(fromNode, toNode))
        {
            carsOnEdge++;
        }     
    }
    return carsOnEdge;
}

//TODO: add better calculation of weights
void update_weights()
{
    int i = 0;
    int carsOnEdge = 0;
    int toNode, fromNode;
    int newWeight = 0;

    for(i = 0; i < numEdges; i++)
    {
        carsOnEdge = get_cars_on_edge(i);
        newWeight = networkEdges[i][2] / carsOnEdge;
    }
}

void check_reroute()
{
    int i = 0, j = 0;
    int fromNode, toNode;
    int edgeId;
    int weight;
    int threshold = 10; //TODO: find actual threshold for rerouting

    for(i = 0; i < N; i++)
    {
        j = 0;
        fromNode = currentStartNode[i];

        while(fromNode != route[i][j] && route[i][j] != -1)
        {
            j++;
        }
        toNode = route[i][j+1];

        edgeId = getEdgeIndex(fromNode, toNode);

        weight = networkEdges[edgeId][3];

        if(weight > threshold)
        {
            reroute[i] = 1;
        }

    }

}

void choose_route(int strategy)
{
    int i = 0;
    int carRoute[nodes] = route[curr_car];
    int destNode;

    reroute[curr_car] = 0;   

    while (i < (nodes-1) && carRoute[i+1] != -1)
    {            
        i++;
    }

    destNode = carRoute[i]; 

    if(strategy == 1)
    {
        i = 0;


    }
    else if (strategy == 2)
    {
        //Keep current route
    }

}

